InputTerminal = require('./inputTerminal'),
Terminal = require('./terminal');

class Game {
    constructor (){
        this.command_manager = new Terminal(this);
        this.input_manager = new InputTerminal(this.command_listener);
        this.input_manager.request_input();

        this.target;
        this.world = [
            {text:"J'ai pris les clés et fermé la porte, si tu veux sortir trouve le double, je ne sais plus où il est !"}
        ];
    }

}

module.exports = Game;
